$(function() {
  /*
  	dynamically activate the navi tab
  */

  var active_class;
  active_class = location.href.split("/")[3];
  if (!active_class) {
    active_class = "home";
  }
  return $("#navi_" + active_class).addClass("active");
});

$(function() {
  /*
  	crawled number process
  */

  var count, crawled_number, new_n, old_n;
  new_n = 100;
  crawled_number = $('#crawled_number');
  old_n = parseInt(crawled_number.html());
  count = 1;
  $.changeNum = function(o_n, n_n) {
    crawled_number.html((n_n - o_n) * count / 5 + o_n);
    if (count !== 5) {
      count += 1;
    } else {
      new_n = new_n + 100;
      old_n = parseInt(crawled_number.html());
      count = 1;
    }
  };
  setInterval((function() {
    return $.changeNum(old_n, new_n);
  }), 100);
});
